import React, { useState } from "react";
import "../CSS/faq.css";
import faqData from "../json/faq.json";

const Faq = () => {
  const [openQuestions, setOpenQuestions] = useState([]);

  const toggleQuestion = (questionIndex) => {
    if (openQuestions.includes(questionIndex)) {
      // Close the question if it's already open
      setOpenQuestions(
        openQuestions.filter((index) => index !== questionIndex)
      );
    } else {
      // Open the question if it's closed
      setOpenQuestions([...openQuestions, questionIndex]);
    }
  };

  return (
    <div className="faq-section">
      <h2>Frequently Asked Questions</h2>
      <ul className="faq-list">
        {faqData.map((item, index) => (
          <li key={index} className="faq-item">
            <h3 onClick={() => toggleQuestion(index)}>
              {item.question}
              {openQuestions.includes(index)
                ? " - (Click to close)"
                : " - (Click to open)"}
            </h3>
            {openQuestions.includes(index) && <p>{item.answer}</p>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Faq;
