// FAQ.js
import React, { useState } from "react";
import "../CSS/faq.css";
import faqData from "../json/faq.json";

function FAQ() {
  const [visibleQuestions, setVisibleQuestions] = useState(4);
  const [openIndex, setOpenIndex] = useState(null);

  const toggleAnswer = (index) => {
    if (openIndex === index) {
      setOpenIndex(null);
    } else {
      setOpenIndex(index);
    }
  };

  const showNextQuestions = () => {
    setVisibleQuestions((prev) => prev + 4);
  };

  return (
    <div className="faq-section">
      <ul className="faq-list">
        {faqData.slice(0, visibleQuestions).map((item, index) => (
          <li key={index} className="faq-item">
            <h3 onClick={() => toggleAnswer(index)}>{item.question}</h3>
            <p className={openIndex === index ? "open" : ""}>{item.answer}</p>
          </li>
        ))}
      </ul>
      {visibleQuestions < faqData.length && (
        <button onClick={showNextQuestions}>Show Next</button>
      )}
    </div>
  );
}

export default FAQ;
