import React, { useState, useEffect } from "react";
import clientsData from "../json/clients.json";
import companiesData from "../json/companies.json";

function YourComponent() {
  const [visibleClients, setVisibleClients] = useState(4);
  const [visibleCompanies, setVisibleCompanies] = useState(4);

  useEffect(() => {
    // Set up a timer to scroll clients and companies
    const timer = setInterval(() => {
      setVisibleClients((prevCount) =>
        prevCount + 1 <= clientsData.length ? prevCount + 1 : 4
      );
      setVisibleCompanies((prevCount) =>
        prevCount + 1 <= companiesData.length ? prevCount + 1 : 4
      );
    }, 3000);

    return () => clearInterval(timer); // Clear the timer on component unmount
  }, []);

  return (
    <div className="container">
      <div className="spacer"></div>
      <div className="box">
        <h2 style={{ color: "blue" }}>Clients</h2>
        <div className="scroll-container">
          {clientsData.slice(0, visibleClients).map((client, index) => (
            <div className="client" key={index}>
              <img
                src={process.env.PUBLIC_URL + client.imageURL}
                alt={client.name}
              />
              <p>{client.name}</p>
            </div>
          ))}
        </div>
      </div>
      <div className="spacer"></div>
      <div className="box">
        <h2 style={{ color: "green" }}>Companies</h2>
        <div className="scroll-container">
          {companiesData.slice(0, visibleCompanies).map((company, index) => (
            <div className="company" key={index}>
              <img
                src={process.env.PUBLIC_URL + company.imageURL}
                alt={company.name}
              />
              <p>{company.name}</p>
            </div>
          ))}
        </div>
      </div>
      <div className="spacer"></div>
    </div>
  );
}

export default YourComponent;
