import React, { useState } from "react";
import clientsData from "../json/clients.json"; // Import JSON data
import companiesData from "../json/companies.json"; // Import JSON data

function YourComponent() {
  const [clientIndex, setClientIndex] = useState(0);
  const [companyIndex, setCompanyIndex] = useState(0);

  const clientsToShow = clientsData.slice(clientIndex, clientIndex + 3);
  const companiesToShow = companiesData.slice(companyIndex, companyIndex + 3);

  const showNextClients = () => {
    setClientIndex((prevIndex) =>
      prevIndex + 1 <= clientsData.length - 3 ? prevIndex + 1 : 0
    );
  };

  const showNextCompanies = () => {
    setCompanyIndex((prevIndex) =>
      prevIndex + 1 <= companiesData.length - 3 ? prevIndex + 1 : 0
    );
  };

  return (
    <div className="containerClient">
      <div className="spacer"></div>
      <div className="box">
        <h2 style={{ color: "blue" }}>Clients</h2>
        <div className="scroll-container">
          {clientsToShow.map((client, index) => (
            <div className="client" key={index}>
              <img
                src={process.env.PUBLIC_URL + client.imageURL}
                alt={client.name}
              />
              <p>{client.name}</p>
            </div>
          ))}
        </div>
        <button onClick={showNextClients}>Next</button>
      </div>
      <div className="spacer"></div>
      <div className="box">
        <h2 style={{ color: "green" }}>Companies</h2>
        <div className="scroll-container">
          {companiesToShow.map((company, index) => (
            <div className="company" key={index}>
              <img
                src={process.env.PUBLIC_URL + company.imageURL}
                alt={company.name}
              />
              <p>{company.name}</p>
            </div>
          ))}
        </div>
        <button onClick={showNextCompanies}>Next</button>
      </div>
      <div className="spacer"></div>
    </div>
  );
}

export default YourComponent;
