import React, { useState } from "react";
import clientsData from "./clients.json"; // Import JSON data
import companiesData from "./companies.json"; // Import JSON data

function YourComponent() {
  const [visibleClients, setVisibleClients] = useState(3);
  const [visibleCompanies, setVisibleCompanies] = useState(3);

  const showNextClients = () => {
    setVisibleClients((prevCount) =>
      prevCount + 1 <= clientsData.length ? prevCount + 1 : 3
    );
  };

  const showNextCompanies = () => {
    setVisibleCompanies((prevCount) =>
      prevCount + 1 <= companiesData.length ? prevCount + 1 : 3
    );
  };

  return (
    <div className="containerClient">
      <div className="spacer"></div>
      <div className="box">
        <h2 style={{ color: "blue" }}>Clients</h2>
        <div className="scroll-container">
          {clientsData.slice(0, visibleClients).map((client, index) => (
            <div className="client" key={index}>
              <img
                src={process.env.PUBLIC_URL + client.imageURL}
                alt={client.name}
              />
              <p>{client.name}</p>
            </div>
          ))}
        </div>
        <button onClick={showNextClients}>Next</button>
      </div>
      <div className="spacer"></div>
      <div className="box">
        <h2 style={{ color: "green" }}>Companies</h2>
        <div className="scroll-container">
          {companiesData.slice(0, visibleCompanies).map((company, index) => (
            <div className="company" key={index}>
              <img
                src={process.env.PUBLIC_URL + company.imageURL}
                alt={company.name}
              />
              <p>{company.name}</p>
            </div>
          ))}
        </div>
        <button onClick={showNextCompanies}>Next</button>
      </div>
      <div className="spacer"></div>
    </div>
  );
}

export default YourComponent;
