// FAQ.js
import React, { useState } from "react";
import "../CSS/faq.css";
import faqData from "../json/faq.json";

function FAQ() {
  const [openIndexes, setOpenIndexes] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  const toggleAnswer = (index) => {
    if (openIndexes.includes(index)) {
      // If the clicked question is already open, close it.
      setOpenIndexes(openIndexes.filter((item) => item !== index));
    } else {
      // Otherwise, open the clicked question.
      if (openIndexes.length < 4) {
        setOpenIndexes([...openIndexes, index]);
      }
    }
  };

  const showNextQuestion = () => {
    if (currentIndex < faqData.length) {
      const newIndexes = openIndexes.slice(1);
      setCurrentIndex(currentIndex + 1);
      setOpenIndexes(newIndexes);
    }
  };

  return (
    <div className="faq-section">
      <h2>Frequently Asked Questions</h2>
      <ul className="faq-list">
        {faqData.map((item, index) => (
          <li
            key={index}
            className={`faq-item ${openIndexes.includes(index) ? "open" : ""}`}
          >
            <h3 onClick={() => toggleAnswer(index)}>{item.question}</h3>
            <p>{item.answer}</p>
          </li>
        ))}
      </ul>
      {currentIndex < faqData.length && (
        <button onClick={showNextQuestion}>Show Next Question</button>
      )}
    </div>
  );
}

export default FAQ;
